function Icon({className = '', disabled = false, name, onClick = null, size = 'd', tip = null, type = 'default'}: {className?: string; disabled?: boolean; name: string; onClick?: ((e: any) => void) | null; size?: string; tip?: string | null; type?: string}) {
    const iconRef = useRef(null)
    const tippyInstanceRef = useRef<TippyInstance | null>(null)
    const tipSignal = signal(tip)

    useEffect(() => {
        if (iconRef.current && tipSignal.value) {
            tippyInstanceRef.current = tippy(iconRef.current, {
                arrow: true,
                content: computed(() => tipSignal.value),
            })
        }
        return () => {
            if (tippyInstanceRef.current) {
                tippyInstanceRef.current.destroy()
            }
        }
    }, [])

    useEffect(() => {
        tipSignal.value = tip
        if (tippyInstanceRef.current) {
            tippyInstanceRef.current.setContent(tip)
        }
    }, [tip])

    return <svg
        className={classnames(
            'c-icon',
            `icon-${size ? size : 'd'}`,
            `type-${type ? type : 'default'}`, {
                click: !!onClick,
                disabled: disabled ? disabled : false,
            }, className,
        )}
        height="40"
        onClick={onClick}
        ref={iconRef}
        viewBox="0 0 24 24"
        width="40"
    >
        <path d={svg[name]} />
    </svg>
}

